def best_of_2(t1, t2, t3) -> float:
    test_scores = [t1, t2, t3]
    best_1 = max(test_scores)
    test_scores.remove(best_1)
    best_2 = max(test_scores)

    return (best_2 + best_1) / 2

def isPalindrome(num: int) -> bool:
    str_num = str(num)

    if str_num == str_num[::-1]:
        return True

    return False


def count_occurrences(num: int) -> None:
    str_num = str(num)
    num_dict = {}
    for i in str_num:
        if not num_dict.get(i):
            num_dict[i] = 1
        else:
            num_dict[i] += 1

    for number, count in num_dict.items():
        print(f"{number} appears {count} times")

